DROP TABLE IF EXISTS `w_articles_content`;
CREATE TABLE `w_articles_content` (
  `ac_id` int(11) NOT NULL AUTO_INCREMENT,
  `aid` int(11) NOT NULL COMMENT '文章id',
  `content` longtext,
  PRIMARY KEY (`ac_id`)
) ENGINE=InnoDB AUTO_INCREMENT=50 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of w_articles_content
-- ----------------------------
INSERT INTO `w_articles_content` VALUES ('24', '7', '&lt;p style=&quot;text-align: center&quot;&gt;&lt;img src=&quot;http://7xs3jl.com1.z0.glb.clouddn.com/jiazhangshouche%20001.jpg&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;');
INSERT INTO `w_articles_content` VALUES ('25', '6', '&lt;p&gt;2016年美术类专业省统考实施细则&lt;/p&gt;&lt;hr/&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;color:#ff0000&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;河南省2016年普通高等学校招生美术类专业省统考实施细则&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;　　一、总则&lt;br/&gt;&lt;br/&gt;　　&lt;/strong&gt;普通高等学校招生美术类专业省级统一考试（以下简称“专业省统考”）是国家教育考试的重要组成部分。2016年我省普通高等学校招生美术类专业考试仍实行全省统一考试的管理体制，遵循科学、公平、安全、准确、规范的原则，贯彻“依法治考，从严治考”的方针，由全省统一命题、统一组织实施考试、统一评卷。为进一步规范专业省统考工作，加大管理力度，结合美术类专业考试的特点和实际情况，特制定本细则。&lt;br/&gt;&lt;strong&gt;&lt;br/&gt;　　二、组织机构及其职责&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;　　（一）省级领导小组及职责&lt;br/&gt;&lt;br/&gt;　　省招办成立美术类专业省统考工作领导小组，主要职责是：&lt;br/&gt;&lt;br/&gt;　　1.依据教育部有关文件精神，制订《河南省2016年普通高等学校招生美术类专业省统考考试实施细则》及相关文件；&lt;br/&gt;　　2.河南省2016年普通高等学校招生专业省统考的命题、制卷、试卷发放管理等；&lt;br/&gt;　　3.指导与协调省辖市及省直管县（市）招办的考务工作；&lt;br/&gt;　　4.专业省统考的评卷组织与管理；&lt;br/&gt;　　5.专业省统考的成绩统计、考生的分数复核等；&lt;br/&gt;　　6.对违纪舞弊事件提出处理意见等。&lt;br/&gt;&lt;br/&gt;　　（二）考区领导小组及其职责&lt;br/&gt;&lt;br/&gt;　　美术类专业省统考以省辖市、省直管县（市）为考区，成立考区专业考试工作领导小组，由本级教育行政部门、招生办公室负责人任主任、副主任，主要职责是：&lt;br/&gt;&lt;br/&gt;　　1.领导、组织、管理本考区的专业考试实施；&lt;br/&gt;　　2.考点的选定、考核；&lt;br/&gt;　　3.考点监考及其他工作人员的选聘；&lt;br/&gt;　　4.试卷（题）的运送、保管、收发及安全保密；&lt;br/&gt;　　5.考区考试所需物品、材料的准备；&lt;br/&gt;　　6.对考生违纪舞弊事件的调查、取证、核实、上报；&lt;br/&gt;　　7.考试期间重大问题的处理；&lt;br/&gt;　　8.完成省级领导小组交办的其它工作；&lt;br/&gt;&lt;br/&gt;　　（三）考点及其职责&lt;br/&gt;&lt;br/&gt;　　考区设若干考点，考点设置在省辖市、省直管县（市）所在地，在考区考试工作领导小组的统一领导下开展以下工作，主要职责是：&lt;br/&gt;　　1.规划、组织并按要求实施专业省统考的准备及各项考务；&lt;br/&gt;　　2.试卷（题）在考点的保管、保密与安全，及时向领导小组报告并处理考试期间发生的各种重大问题；&lt;br/&gt;　　3.考点所有工作人员的培训；&lt;br/&gt;　　4.组织考生有序进入和离开考场，切实维护考场秩序；&lt;br/&gt;　　5.各考场试卷（题）及有关材料的回收、验收，确保准确无误；&lt;br/&gt;　　6.按要求设置公告栏、考点平面图，及时向考生公布考试的有关规定、考试纪律和考试要求；&lt;br/&gt;　　7.按要求设置考务、宣传、保卫、后勤、医疗等若干小组，分管有关工作，以保证考试正常实施；&lt;br/&gt;&lt;br/&gt;　　&lt;strong&gt;三、命题&lt;br/&gt;&lt;/strong&gt;&lt;br/&gt;　　美术类专业省统考科目考试为素描、速写、色彩三科。每科满分为100分。&lt;br/&gt;&lt;br/&gt;　　（一）命题原则&lt;br/&gt;&lt;br/&gt;　　1.全省统一命题；&lt;br/&gt;　　2.坚持正确导向，依据专业考试科目，体现出学习美术类专业对考生所具备的基本专业要求；&lt;br/&gt;　　3.坚持标准，难易适度，题目要在规定的时间内，客观准确测试出美术类专业考生的基本素质和技能，测试考生的艺术修养、审美、观察、造型、表现等能力和技法的运用能力，有利于高校选拔专业优秀学生。&lt;br/&gt;&lt;br/&gt;　　（二）命题的范围&lt;br/&gt;&lt;br/&gt;　　1.速写：人物动态、人物动态组合、人物与场景组合；&lt;br/&gt;　　2.素描：人物头像、石膏像、静物；&lt;br/&gt;　　3.色彩：静物组合、风景。&lt;br/&gt;&lt;br/&gt;　　（三）考试形式&lt;br/&gt;&lt;br/&gt;　　1.写生；&lt;br/&gt;　　2.表现由考场提供的图片或相关资料所规定的内容；&lt;br/&gt;　　3.默写文字材料所描述的内容。&lt;br/&gt;&lt;br/&gt;　　（四）命题工作的组织管理&lt;br/&gt;&lt;br/&gt;　　1.由省招办组织有关专家命题；&lt;br/&gt;　　2.命题工作实行封闭管理；&lt;br/&gt;　　3.试题在启封前和使用过程中至考试结束属国家级机密材料（含考试所用的相关素材）。&lt;br/&gt;&lt;br/&gt;　　&lt;strong&gt;四、考试&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;　　考试期间，各级招办应安排工作人员昼夜值班，值班电话应在考前告知上下级招办。对外设置举报信箱、公布考试期间的省、市举报电话。&lt;br/&gt;如发生失密、泄密、大面积作弊等重大事件，应及时采取有效措施控制，并立即报告省招办。&lt;br/&gt;&lt;br/&gt;　　(一)考点考场布置&lt;br/&gt;&lt;br/&gt;　　1.考点考场各项准备工作，在主考的主持下进行。&lt;br/&gt;　　2.考点配备主考1人，副主考2人，每考场配备监考员2人。主考、副主考、监考员及其他工作人员应佩证上岗；&lt;br/&gt;　　3.考点应设置考务组（负责考务安排，试卷安全保密、保管，试卷收发、装订）、宣传组（负责考点宣传）、保卫组（负责考点及考场周边安全保卫）、后勤组（负责考试所需物品的供应及供电照明）、医务组（负责考生医疗救治）。&lt;br/&gt;　　4.考点应有明确的考点平面图、路标、公告栏及其它标识，同时还应将《河南省2016年普通高等学校招生美术类专业省统考时间表》、《河南省2016年普通高等学校招生美术类省统考考生守则》、《国家教育考试违规处理办法》等张贴在考点的醒目位置，并公布举报电话，接受考生监督。&lt;br/&gt;　　5.按照规范化要求布置考点。考点气氛要庄重，使考生感到严肃、亲切、和谐。&lt;br/&gt;　　考点大门悬挂红色横幅，统一用字为“河南省2016年普通高等学校招生美术类专业省统考××考区××××考点”。&lt;br/&gt;　　考点内张贴具有欢迎和鼓励意义的标语，如：&lt;br/&gt;　　——热烈欢迎您参加河南省2016年普通高等学校招生美术类专业省统考；&lt;br/&gt;　　——祝参加美术类专业省统考的广大考生考出优异成绩；&lt;br/&gt;　　——今日莘莘学子，明朝国家栋梁；&lt;br/&gt;　　——信守诺言，诚信考试；遵守考纪，公平竞争；积极拼搏，考出水平。&lt;br/&gt;　　6.考点内设若干考场。考点应根据美术类专业考试的特点与要求，统一规划安排考点所有考场的设置。考场应安全、安静、通风、采光条件好，统一在考场前方配置挂钟，考前按北京时间校准。场内除必备物品外，不得留有其他可能影响考试的物品、字迹和图画等。&lt;br/&gt;　　7.每考场30人。考生座位单人、单桌、单行排列，间距80厘米以上。&lt;br/&gt;　　8.美术类专业省统考时间：&lt;span style=&quot;font-size: 20px;font-family: 仿宋&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;table cellspacing=&quot;0&quot; cellpadding=&quot;0&quot;&gt;&lt;tbody&gt;&lt;tr style=&quot;height: 59px&quot; class=&quot;firstRow&quot;&gt;&lt;td style=&quot;border-color: windowtext; border-width: 1px; padding: 0px 7px;&quot; width=&quot;132&quot; valign=&quot;top&quot; height=&quot;59&quot;&gt;&lt;p style=&quot;;line-height: 37px&quot;&gt;&lt;span style=&quot;font-size: 20px;font-family: 仿宋&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;span style=&quot;font-size: 20px;font-family: 仿宋&quot;&gt;时间&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;;line-height: 37px&quot;&gt;&lt;span style=&quot;font-size: 20px;font-family: 仿宋&quot;&gt;日期&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;border-color: windowtext windowtext windowtext rgb(212, 208, 200); border-top-width: 1px; border-right-width: 1px; border-bottom-width: 1px; padding: 0px 7px;&quot; width=&quot;204&quot; height=&quot;59&quot;&gt;&lt;p style=&quot;text-align: center;line-height: 37px&quot;&gt;&lt;span style=&quot;font-size: 20px;font-family: 仿宋&quot;&gt;上午&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;border-color: windowtext windowtext windowtext rgb(212, 208, 200); border-top-width: 1px; border-right-width: 1px; border-bottom-width: 1px; padding: 0px 7px;&quot; width=&quot;192&quot; height=&quot;59&quot;&gt;&lt;p style=&quot;text-align: center;line-height: 37px&quot;&gt;&lt;span style=&quot;font-size: 20px;font-family: 仿宋&quot;&gt;下午&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr style=&quot;height: 61px&quot;&gt;&lt;td style=&quot;border-color: rgb(212, 208, 200) windowtext windowtext; border-right-width: 1px; border-bottom-width: 1px; padding: 0px 7px; border-left-width: 1px;&quot; width=&quot;132&quot; height=&quot;61&quot;&gt;&lt;p style=&quot;text-align: center;line-height: 37px&quot;&gt;&lt;span style=&quot;font-size: 20px;font-family: 仿宋&quot;&gt;2015&lt;/span&gt;&lt;span style=&quot;font-size: 20px;font-family: 仿宋&quot;&gt;年&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center;line-height: 37px&quot;&gt;&lt;span style=&quot;font-size: 20px;font-family: 仿宋&quot;&gt;12&lt;/span&gt;&lt;span style=&quot;font-size: 20px;font-family: 仿宋&quot;&gt;月19日&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;border-color: rgb(212, 208, 200) windowtext windowtext rgb(212, 208, 200); border-right-width: 1px; border-bottom-width: 1px; padding: 0px 7px;&quot; width=&quot;204&quot; valign=&quot;top&quot; height=&quot;61&quot;&gt;&lt;p style=&quot;;line-height: 37px;text-indent: 10px&quot;&gt;&lt;span style=&quot;font-size: 20px;font-family: 仿宋&quot;&gt;速写&amp;nbsp; 8:30-9：00&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;;line-height: 37px;text-indent: 10px&quot;&gt;&lt;span style=&quot;font-size: 20px;font-family: 仿宋&quot;&gt;素描&amp;nbsp; 9:00-12:00&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;border-color: rgb(212, 208, 200) windowtext windowtext rgb(212, 208, 200); border-right-width: 1px; border-bottom-width: 1px; padding: 0px 7px;&quot; width=&quot;192&quot; height=&quot;61&quot;&gt;&lt;p style=&quot;text-align: center;line-height: 37px&quot;&gt;&lt;span style=&quot;font-size: 20px;font-family: 仿宋&quot;&gt;色彩 &amp;nbsp;14:00-17：00&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;&lt;br/&gt;　　9.考生在考试前一天的下午熟悉考场。考点和考场应在考生熟悉考场之前准备完毕。&lt;br/&gt;&lt;br/&gt;　　（二）监考员的选拔、培训及其职责&lt;br/&gt;&lt;br/&gt;　　1.监考员由考区领导小组选拔、聘任，工作的考场随机确定。&lt;br/&gt;　　2.监考员应具备的基本条件为：思想品德良好，作风正派，遵纪守法；熟悉考试业务，工作认真负责；身体健康；遵守保密工作规定。监考员有亲属参加考试的要主动提出实行回避制度。&lt;br/&gt;　　3.考点考前按规定对全体工作人员集中进行学习和培训，要求每一位工作人员明确职责和分工，熟悉各种规章和考试纪律，特别对监考员应进行系统的业务培训，要求每一位监考员应当掌握试卷（试题）的分发、回收、静物的摆放等要点和步骤。&lt;br/&gt;　　4.未经培训的人员不得参加监考和其它考务工作。&lt;br/&gt;　　&lt;br/&gt;　　5.监考员职责：&lt;br/&gt;　　⑴在主考主持下，负责本考场的考试，维持考场秩序，保证考试正常进行；&lt;br/&gt;　　⑵使用金属探测器对考生进行违禁物品的检查，适时开启和关闭无线信号屏蔽器；&lt;br/&gt;　　⑶对考生进行考风考纪教育，宣读《考生守则》，宣布考试注意事项；&lt;br/&gt;　　⑷检查考生专业准考证、身份证，严查替考；&lt;br/&gt;　　⑸监督考生按规定作画，制止违纪、作弊行为，并按要求如实记载考场情况及考生违纪舞弊情况，对考生用于作弊的材料、工具等予以收缴或暂扣；&lt;br/&gt;　　⑹对在考试中发现的异常情况应立即报告主考，不得扩散；&lt;br/&gt;　　⑺遵守监考员“三项纪律”和“十项注意”；&lt;br/&gt;　　⑻不得将手机、手表等通讯、计时工具带入考场。&lt;br/&gt;&lt;br/&gt;　　6.《考生守则》：&lt;br/&gt;　　⑴考生必须自觉服从监考员管理，不得以任何理由妨碍监考员等考试工作人员履行职责，不得扰乱考场及其他考试工作地点秩序。&lt;br/&gt;　　⑵凭专业准考证、身份证按规定时间和地点参加考试。&lt;br/&gt;　　⑶开考前25分钟（第一科前移10分钟）预备铃响后开始入场，10分钟内入场完毕。开考15分钟后，迟到考生不能进场。考试结束后方可交卷出场。未参加速写考试的考生，可在素描开考前5分钟入场参加素描考试。&lt;br/&gt;　　⑷考生入场只能携带必备的工具和画夹，不能携带书报、资料和画架。严禁携带各种无线通讯工具、电子存储记忆录放设备以及手表或其他计时工具、涂改液、修正带等物品进入考场。&lt;br/&gt;　　⑸考生入场后，对号入座。考生领到答卷纸后，应认真核对条形码上的姓名、考生号、身份证号、考场号和座位号，并在指定位置和规定的时间内准确清楚地填写姓名、性别、考生号、考场号和座位号等。&lt;br/&gt;　　⑹开考信号发出后才能开始作画，考试终了信号发出后停止作画，并根据监考员指令依次退出考场。不准在考场逗留，不准将答卷纸、试题带出考场。&lt;br/&gt;　　⑺严格按照题目要求在指定的区域作画，严禁在答卷纸上做任何标记，否则，按做标记卷处理。&lt;br/&gt;　　⑻在考场内必须保持安静，不准吸烟，不准喧哗，不准交头接耳、左顾右盼、打手势、做暗号，不准夹带、旁窥、抄袭或有意让他人抄袭，不准交换试题、答卷纸，不得自行传递文具、用品等。&lt;br/&gt;　　⑼如不遵守考试纪律，不服从考试工作人员管理，有违纪作弊行为的，将依法按照《中华人民共和国刑法（修正案九）》第二百八十四条中的相关条款、《国家教育考试违规处理办法》及教育部年度招生有关规定进行处理，并将违规事实记入考生考试诚信档案。&lt;br/&gt;&lt;br/&gt;　　（三）考试要求&lt;br/&gt;&lt;br/&gt;　　1.考生必须使用考场提供的试卷；&lt;br/&gt;　　2.考生素描、速写只能使用铅笔、炭笔作为表现工具，色彩用水粉或水粉颜料；&lt;br/&gt;　　3.考生自备画板或画夹及相关的绘画工具；&lt;br/&gt;　　4.考生必须按试题规定及要求完成试卷，不得增加或减少考试内容；&lt;br/&gt;　　5.表现手法不限；&lt;br/&gt;　　6.试卷完成后不允许在画面上喷洒任何固定液体；&lt;br/&gt;　　7.素描、速写、色彩三科答卷作画部分规格为：32cm×30cm；&lt;br/&gt;　　8.考试结束后试题随试卷上交，不得带出考场。&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;　　五、考试实施&lt;/strong&gt;&lt;br/&gt;&lt;br/&gt;　　1.每科开考前40分钟（第一科开考前50分钟），监考员和有关人员到达考务办公室，接受主考临场指示，按北京时间核准钟表，领取试卷（题）及有关工作用品；&lt;br/&gt;　　2.开考前25分钟（第一科考试开考前35分钟）组织考生有序入场，一名监考员使用金属探测仪检查违禁物品，劝阻考生不要携带规定以外的物品入场；另一名监考员逐个核对考生的相貌与准考证、身份证上的照片是否相符，并指导考生对号入座；&lt;br/&gt;　　3.开考前10分钟，开始启封分发试卷（题）。第一科考试开考前监考员还要向考生宣读《考生守则》、《中华人民共和国刑法（修正案九）》第二百八十四条中的相关条款和《国家教育考试违规处理办法》及有关规定的相关内容；&lt;br/&gt;　　4.监考员应提醒考生认真核对答卷纸上的个人信息是否正确，无误后按规定的时间开始作画；&lt;br/&gt;　　5.速写、素描两科合并一次连续考完。速写考试结束后，监考员应先收速写答卷（试题），后发素描试卷（试题），接着考试，考生不休息；&lt;br/&gt;　　6.开考后，监考员应逐份试卷检查考生填写的姓名、性别、考生号、考场号、座号与座次表上是否一致，并督促考生正确填写。&lt;br/&gt;　　7.开考15分钟后，迟到考生不得进场。监考员应在缺考考生的答卷纸的“姓名”栏内填写“缺考”字样；&lt;br/&gt;　　8.考试结束前15分钟吹一次哨，监考员提醒考生注意离考试结束还有15分钟，注意掌握好时间；&lt;br/&gt;　　9.考试期间监考员应分立考场两端，密切注视考生作画，防止考生交换答卷或其他舞弊行为；&lt;br/&gt;　　10.考试结束铃响后，考生应停止作画，监考员将试卷（试题）收齐清点无误后，方可允许考生离开考场；&lt;br/&gt;　　11.考试结束后监考员密封考生答卷。仅在密封条上刷浆糊和胶水，不可以在答卷纸密封线内刷浆糊和胶水。缺考考生答卷和尾场空白答卷不密封，违纪考生答卷密封。每场收回的试卷袋内均为30份答卷；&lt;br/&gt;　　12.监考员应将缺考考生和违纪考生的考生号、姓名及违规事实分别填写在答卷封袋上和《河南省2016年普通高校招生艺术类专业省统考记录表》上。&lt;br/&gt;&lt;br/&gt;&lt;strong&gt;　　六、评卷工作&lt;br/&gt;&lt;/strong&gt;&lt;br/&gt;　　（一）评卷工作由省招办统一组织。省招办和有关专家组成评卷工作领导小组。下设办公室、学科评卷组、答卷保管组、专家复审组等。评委由省招办在省内各招生院校推荐上报的评委库中统一选聘。&lt;br/&gt;&lt;br/&gt;　　（二）评委应具备下列条件：&lt;br/&gt;&lt;br/&gt;　　1.坚持原则，遵守纪律，服从领导，工作责任心强；&lt;br/&gt;　　2.业务熟练，原则上应具有中级以上职称；&lt;br/&gt;　　3.身体健康，能胜任专业省统考评卷工作；&lt;br/&gt;　　4.当年无子女或直系亲属参加该次专业考试。&lt;br/&gt;&lt;br/&gt;　　（三）工作纪律：&lt;br/&gt;&lt;br/&gt;　　1.评卷教师经省招办聘任后，其身份应予保密，任何单位或个人不得对外宣传；&lt;br/&gt;　　2.评卷教师应根据评卷小组制定的学科评分执行细则对答卷进行公平、公正的分类分等评分，避免主观性和随意性；&lt;br/&gt;　　3.不得将评卷的有关情况对外透露；&lt;br/&gt;　　4.在评卷工作期间手机等通讯工具须集中管理，特殊情况用指定的固定电话进行联系；&lt;br/&gt;　　5.不得擅自带走答卷或给答卷拍照；&lt;br/&gt;　　6.服从评卷小组的统一安排，严格遵守工作时间，不得擅离职守；&lt;br/&gt;　　7.评卷期间所有工作人员一律凭证进入评卷场地；&lt;br/&gt;　　8.评卷期间发现评委有违纪舞弊行为，立即停止其评委工作，除按规定处理外，还要将违纪舞弊事实记入专业评委数据库档案内，今后不再聘为艺术类专业省统考评委。&lt;br/&gt;&lt;br/&gt;　　（四）评卷办法&lt;br/&gt;&lt;br/&gt;　　评卷领导小组应根据教育部《2016年全国普通高校招生美术类专业省统考评分参考》要求，制定我省美术类专业省统考评分执行细则。美术类评卷按照“先分类分等，后细分打分”的原则。各科答卷均实行密封、流水作业的评卷办法。&lt;br/&gt;&lt;br/&gt;　　1.评选样卷：评卷组根据美术类各科省统考评分执行细则，参照考生答卷选出各类答卷的样卷；&lt;br/&gt;　　2.各科评委对照省统考评分执行细则和各类样卷标准集体分类、分等，共分六大类16个评分等级；&lt;br/&gt;　　3.评委分类分等后的答卷由专家复审组进行复查，最后确定答卷等级；&lt;br/&gt;　　4.经专家复审后的答卷由各科评委在确定等级规定的分数范围内各自打分，5个评委同时打分，所打分数输入微机去掉一个最高分和一个最低分，加总平均后即为该科最后得分；&lt;br/&gt;　　5.答卷保管组核对违纪考试记录表并输入微机。&lt;br/&gt;&lt;br/&gt;　&lt;strong&gt;　七、其他有关事宜&lt;br/&gt;&lt;/strong&gt;&lt;br/&gt;　　省招办将在河南省招生办公室网站上提供考生艺术类专业省统考成绩查询系统，并提供该类专业省统考分数段和专业合格线，对专业合格考生将不再另行发放专业合格证。在高考文化成绩公布后，省招办按照全省该类本（分A、B两段）、专科招生计划的适当比例划定本（分A、B两段）、专科录取的专业控制分数线和文化控制分数线。对在专业省统考中被认定为考试违规的考生，将依法按照《中华人民共和国刑法（修正案九）》第二百八十四条中的相关条款、《国家教育考试违规处理办法》和教育部年度招生有关规定予以处理，并将违规事实记入考生考试诚信档案。&lt;/p&gt;');
INSERT INTO `w_articles_content` VALUES ('26', '8', '&lt;p style=&quot;text-align: center&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;text-align: center;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center&quot;&gt;&lt;img src=&quot;http://7xs3jl.com1.z0.glb.clouddn.com/xiaokaozheng%E6%AF%9B%E9%A3%8E%E5%B0%98%20%E5%A4%AE%E7%BE%8E%20-%20%E5%89%AF%E6%9C%AC.jpg&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 黑体, SimHei; font-size: 36px;&quot;&gt;&lt;strong&gt;毛风尘 &amp;nbsp; &amp;nbsp; &amp;nbsp;通过中央美院 全国31名&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 黑体, SimHei; font-size: 36px;&quot;&gt;&lt;strong&gt;&lt;br/&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;span style=&quot;font-family: 黑体, SimHei; font-size: 36px;&quot;&gt;&lt;strong&gt;&lt;img src=&quot;http://7xs3jl.com1.z0.glb.clouddn.com/xiaokaozheng%E4%BD%95%E4%BC%A0%E7%8E%BA%E5%A4%AE%E7%BE%8E.jpg&quot;/&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 黑体, SimHei; font-size: 36px;&quot;&gt;&lt;strong&gt;&lt;br/&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:黑体, SimHei&quot;&gt;&lt;span style=&quot;font-size: 36px;&quot;&gt;&lt;strong&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; 中央美术学院 &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;何传玺&lt;/strong&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 黑体, SimHei; font-size: 18px;&quot;&gt;&lt;strong&gt;&lt;br/&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/p&gt;&lt;p style=&quo