DROP TABLE IF EXISTS `w_category`;
CREATE TABLE `w_category` (
  `cid` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `cate_order` int(11) DEFAULT '0',
  `c_name` varchar(255) DEFAULT NULL COMMENT '分类名',
  `list_cate` tinyint(1) DEFAULT '0' COMMENT '列表种类：0-文章，1-图片',
  PRIMARY KEY (`cid`)
) ENGINE=InnoDB AUTO_INCREMENT=17 DEFAULT CHARSET=utf8 COMMENT='分类表';

