INSERT INTO `w_portalpage` VALUES ('1', '画室起源', '<p><span style=\"color: rgb(89, 89, 89); font-family: 微软雅黑,Microsoft YaHei; font-size: 24px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"color: rgb(192, 80, 77); font-family: 微软雅黑,Microsoft YaHei; font-size: 24px;\">沿途<span style=\"font-family: 微软雅黑,Microsoft YaHei; font-size: 24px;\">画室</span></span>隶属于<span style=\"color: rgb(192, 80, 77); font-family: 微软雅黑,Microsoft YaHei; font-size: 24px;\">开封沿途教育咨询服务有限公司</span>。开封沿途教育咨询服务有限公司是集线下实体美术培训、线上美术课程研发、艺考咨询、作品交流互动的高端教育培训机构。开封沿途画室是以央美、清华美院、国美、川美等国内著名高校为依托的专业画室。是一所中国一流的高端美术培训机构、全日制的食宿一体化美术培训学校，美术专业的师资都拥有丰富的教学经验和完整的教学体系。办学规范，口碑信誉俱佳，学习氛围浓厚。<span style=\"color: rgb(89, 89, 89); font-family: 微软雅黑,Microsoft YaHei;\">&nbsp;</span></span></p><p style=\"text-indent: 35px;\"><span style=\"color: rgb(89, 89, 89); font-family: 微软雅黑,Microsoft YaHei; font-size: 24px;\">&nbsp; 教学占地3000多平米，拥有采光良好的玻璃教室和专门的文化课教室，先进的多媒体教学，图书资料，寝室配有空调、柜子、桌子、温馨舒适，开水房、洗衣房、配有美术用品服务部、生活后勤部。</span></p><p><br/></p>', '2017-01-10 16:14:15');
