INSERT INTO `w_portalpage` VALUES ('4', '联系我们', '<p><span style=\"color: rgb(127, 127, 127); font-family: 微软雅黑,Microsoft YaHei; font-size: 20px;\">新校区地址：开封市龙亭区西关北街孙李唐村八号</span></p><p><span style=\"color: rgb(127, 127, 127); font-family: 微软雅黑,Microsoft YaHei; font-size: 20px;\">垂询电话&nbsp;&nbsp;张校长：18903781265 &nbsp;&nbsp;qq:727248092&nbsp;</span></p><p><span style=\"color: rgb(127, 127, 127); font-family: 微软雅黑,Microsoft YaHei; font-size: 20px;\">画室官网：</span><a style=\"font-family: 微软雅黑,Microsoft YaHei; font-size: 20px; text-decoration: underline;\" href=\"http://www.yantujy.net\"><span style=\"font-family: 微软雅黑,Microsoft YaHei; font-size: 20px;\">www.yantujy.com</span></a></p>', '2017-02-10 12:57:39');
INSERT INTO `w_portalpage` VALUES ('5', '精品班', '<h1 style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\" label=\"标题居中\"><span style=\"font-size: 24px;\"><span style=\"color: rgb(51, 153, 204); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 36px; font-style: italic; font-weight: bold;\"><strong>15</strong></span><span style=\"color: rgb(51, 153, 204); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 24px; font-style: italic; font-weight: bold;\"><strong>人精品小班教学<span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 24px; font-style: italic; font-weight: bold;\">（二本过关率<span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 36px; font-style: italic; font-weight: bold;\">100%</span>）</span></strong></span></span></h1><p>&nbsp;</p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(51, 153, 204); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 36px; font-style: italic; font-weight: bold;\"><strong>一对一</strong></span><span style=\"color: rgb(51, 153, 204); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 24px; font-style: italic; font-weight: bold;\"><strong>个性化辅导</strong></span><span style=\"color: rgb(51, 153, 204); line-height: 18px; font-size: 24px; font-style: italic; font-weight: bold;\"></span><span style=\"color: rgb(51, 153, 204); line-height: 18px; font-size: 16px; font-style: italic; font-weight: bold;\"></span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\"><strong>确保学员每天进步1%；</strong></span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\"><strong>入学测试（有门槛、有要求、有态度）；</strong></span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\"><strong>制订方案（个性化、专业化、人性化）；</strong></span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\"><strong>每天改画（十几次、抓重点、看实效）；</strong></span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\"><strong>每天评画（五十次、高水平、重基础）；</strong></span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\"><strong>每周辅导（一对一、两三次、保姆式）；</strong></span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\"><strong>每周反馈（与家长勤沟通、多联系）。</strong></span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(51, 153, 204); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\"><br/></span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">画室对比：</span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(147, 137, 83); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">普通画室：一个老师教40个学生</span><span style=\"line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">&nbsp;</span><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\">沿途画室：一个老师教<span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 20px; font-style: italic; font-weight: bold;\">15</span>个学生</span><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">&nbsp;</span><span style=\"line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">屌丝画室：一个老师教所有学生</span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(147, 137, 83); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">普通画室：一个老师每天点评5次</span><span style=\"line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">&nbsp;&nbsp;</span><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\">沿途画室：一个老师每天点评<span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 20px; font-style: italic; font-weight: bold;\">50</span>次</span><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">&nbsp;</span><span style=\"line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">屌丝画室：一个老师每天点评1次</span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(147, 137, 83); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">普通画室：老师每月向家长反馈一次</span><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\">沿途画室：老师每</span><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 20px; font-style: italic; font-weight: bold;\">周</span><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\">向家长反馈一次</span><span style=\"line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">屌丝画室：老师每年向家长反馈一次</span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(147, 137, 83); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">普通画室：老师每天改画3次</span><span style=\"line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">&nbsp;</span><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\">沿途画室：老师每天改画<span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 20px; font-style: italic; font-weight: bold;\">20</span>次</span><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">&nbsp;</span><span style=\"line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">屌丝画室：老师从不改画</span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom: 2px solid rgb(204, 204, 204);\"><span style=\"color: rgb(147, 137, 83); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">普通画室：老师对学生每天说10句话 </span><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\">沿途画室：老师对学生每天说<span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 20px; font-style: italic; font-weight: bold;\">100</span>句话 </span><span style=\"line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">屌丝画室：老师基本等于哑巴</span></p>', '2017-02-10 12:58:22');
INSERT INTO `w_portalpage` VALUES ('6', '强化班', '<h1 style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\" label=\"标题居中\"><span style=\"font-size: 24px;\"><span style=\"color: rgb(51, 153, 204); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 36px; font-style: italic; font-weight: bold;\"><strong>10</strong></span><span style=\"color: rgb(51, 153, 204); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 24px; font-style: italic; font-weight: bold;\"><strong>人强化小班教学</strong></span></span></h1><h1 style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\" label=\"标题居中\"><span style=\"font-size: 24px;\"><span style=\"color: rgb(51, 153, 204); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 24px; font-style: italic; font-weight: bold;\"><strong><span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 24px; font-style: italic; font-weight: bold;\">（<span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 24px; font-style: italic; font-weight: bold;\">985</span>、<span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 24px; font-style: italic; font-weight: bold;\">211</span>重点大学及<span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 24px; font-style: italic; font-weight: bold;\">鲁美</span>、<span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 24px; font-style: italic; font-weight: bold;\">西美</span>、<span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 24px; font-style: italic; font-weight: bold;\">川美</span>、<span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 24px; font-style: italic; font-weight: bold;\">广美</span>、<span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 24px; font-style: italic; font-weight: bold;\">湖美</span>过关率<span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 36px; font-style: italic; font-weight: bold;\">100%</span>）</span></strong></span></span></h1><p>&nbsp;</p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(51, 153, 204); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 36px; font-style: italic; font-weight: bold;\"><strong>一对一</strong></span><span style=\"color: rgb(51, 153, 204); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 24px; font-style: italic; font-weight: bold;\"><strong>个性化辅导</strong></span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 24px; font-style: italic; font-weight: bold;\">（定期<span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 24px; font-style: italic; font-weight: bold;\">美院老师</span>及<span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 24px; font-style: italic; font-weight: bold;\">重点高校教授</span>来校密训）</span><span style=\"color: rgb(51, 153, 204); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 24px; font-style: italic; font-weight: bold;\"></span><span style=\"color: rgb(51, 153, 204); line-height: 18px; font-size: 24px; font-style: italic; font-weight: bold;\"></span><span style=\"color: rgb(51, 153, 204); line-height: 18px; font-size: 16px; font-style: italic; font-weight: bold;\"></span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\"><strong>确保学员每天进步1%；</strong></span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\"><strong>入学测试（有门槛、有要求、有态度）；</strong></span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\"><strong>制订方案（个性化、专业化、人性化）；</strong></span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\"><strong>每天改画（十几次、抓重点、看实效）；</strong></span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\"><strong>每天评画（五十次、高水平、重基础）；</strong></span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\"><strong>每周辅导（一对一、两三次、保姆式）；</strong></span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\"><strong>每周反馈（与家长勤沟通、多联系）。</strong></span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(51, 153, 204); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\"><br/></span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">画室对比：</span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(147, 137, 83); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">普通画室：一个老师教40个学生</span><span style=\"line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">&nbsp;</span><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\">沿途画室：一个老师教<span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 20px; font-style: italic; font-weight: bold;\">10</span>个学生</span><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">&nbsp;</span><span style=\"line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">屌丝画室：一个老师教所有学生</span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(147, 137, 83); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">普通画室：一个老师每天点评5次</span><span style=\"line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">&nbsp;&nbsp;</span><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\">沿途画室：一个老师每天点评<span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 20px; font-style: italic; font-weight: bold;\">50</span>次</span><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">&nbsp;</span><span style=\"line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">屌丝画室：一个老师每天点评1次</span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(147, 137, 83); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">普通画室：老师每月向家长反馈一次</span><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\">沿途画室：老师每</span><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 20px; font-style: italic; font-weight: bold;\">周</span><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\">向家长反馈一次</span><span style=\"line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">屌丝画室：老师每年向家长反馈一次</span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(147, 137, 83); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">普通画室：老师每天改画3次</span><span style=\"line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">&nbsp;</span><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\">沿途画室：老师每天改画<span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 20px; font-style: italic; font-weight: bold;\">20</span>次</span><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">&nbsp;</span><span style=\"line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">屌丝画室：老师从不改画</span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom: 2px solid rgb(204, 204, 204);\"><span style=\"color: rgb(147, 137, 83); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">普通画室：老师对学生每天说10句话 </span><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\">沿途画室：老师对学生每天说<span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 20px; font-style: italic; font-weight: bold;\">100</span>句话 </span><span style=\"line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">屌丝画室：老师基本等于哑巴</span></p>', '2017-02-10 12:58:46');
INSERT INTO `w_portalpage` VALUES ('7', '定向班', '<h1 style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\" label=\"标题居中\"><span style=\"font-size: 24px;\"><span style=\"color: rgb(51, 153, 204); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 36px; font-style: italic; font-weight: bold;\"><strong>10</strong></span><span style=\"color: rgb(51, 153, 204); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 24px; font-style: italic; font-weight: bold;\"><strong>人定向小班教学</strong></span></span></h1><h1 style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\" label=\"标题居中\"><span style=\"font-size: 24px;\"><span style=\"color: rgb(51, 153, 204); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 24px; font-style: italic; font-weight: bold;\"><strong><span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 24px; font-style: italic; font-weight: bold;\">（<span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 24px; font-style: italic; font-weight: bold;\">央美<span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 24px; font-style: italic; font-weight: bold;\">，</span>清华<span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 24px; font-style: italic; font-weight: bold;\">，</span>国美<span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 24px; font-style: italic; font-weight: bold;\">及</span>中国人民大学<span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 24px; font-style: italic; font-weight: bold;\">，</span>北京服装学院</span>过关率<span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 36px; font-style: italic; font-weight: bold;\">100%</span>）</span></strong></span></span></h1><p>&nbsp;</p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(51, 153, 204); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 36px; font-style: italic; font-weight: bold;\"><strong>一对一</strong></span><span style=\"color: rgb(51, 153, 204); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 24px; font-style: italic; font-weight: bold;\"><strong>个性化辅导</strong></span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 24px; font-style: italic; font-weight: bold;\">（定期<span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 24px; font-style: italic; font-weight: bold;\">美院老师</span>及<span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 24px; font-style: italic; font-weight: bold;\">重点高校教授</span>来校密训）</span><span style=\"color: rgb(51, 153, 204); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 24px; font-style: italic; font-weight: bold;\"></span><span style=\"color: rgb(51, 153, 204); line-height: 18px; font-size: 24px; font-style: italic; font-weight: bold;\"></span><span style=\"color: rgb(51, 153, 204); line-height: 18px; font-size: 16px; font-style: italic; font-weight: bold;\"></span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\"><strong>确保学员每天进步1%；</strong></span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\"><strong>入学测试（有门槛、有要求、有态度）；</strong></span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\"><strong>制订方案（个性化、专业化、人性化）；</strong></span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\"><strong>每天改画（十几次、抓重点、看实效）；</strong></span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\"><strong>每天评画（五十次、高水平、重基础）；</strong></span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\"><strong>每周辅导（一对一、两三次、保姆式）；</strong></span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(0, 0, 0); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\"><strong>每周反馈（与家长勤沟通、多联系）。</strong></span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(51, 153, 204); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\"><br/></span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">画室对比：</span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(147, 137, 83); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">普通画室：一个老师教40个学生</span><span style=\"line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">&nbsp;</span><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\">沿途画室：一个老师教<span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 20px; font-style: italic; font-weight: bold;\">10</span>个学生</span><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">&nbsp;</span><span style=\"line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">屌丝画室：一个老师教所有学生</span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(147, 137, 83); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">普通画室：一个老师每天点评5次</span><span style=\"line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">&nbsp;&nbsp;</span><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\">沿途画室：一个老师每天点评<span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 20px; font-style: italic; font-weight: bold;\">50</span>次</span><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">&nbsp;</span><span style=\"line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">屌丝画室：一个老师每天点评1次</span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(147, 137, 83); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">普通画室：老师每月向家长反馈一次</span><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\">沿途画室：老师每</span><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 20px; font-style: italic; font-weight: bold;\">周</span><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\">向家长反馈一次</span><span style=\"line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">屌丝画室：老师每年向家长反馈一次</span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom-color: rgb(204, 204, 204); border-bottom-width: 2px; border-bottom-style: solid;\"><span style=\"color: rgb(147, 137, 83); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">普通画室：老师每天改画3次</span><span style=\"line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">&nbsp;</span><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\">沿途画室：老师每天改画<span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 20px; font-style: italic; font-weight: bold;\">20</span>次</span><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">&nbsp;</span><span style=\"line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">屌丝画室：老师从不改画</span></p><p style=\"margin: 0px 0px 20px; padding: 0px 4px 0px 0px; text-align: center; line-height: normal; font-size: 32px; font-weight: bold; border-bottom: 2px solid rgb(204, 204, 204);\"><span style=\"color: rgb(147, 137, 83); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">普通画室：老师对学生每天说10句话 </span><span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 18px; font-style: italic; font-weight: bold;\">沿途画室：老师对学生每天说<span style=\"color: rgb(192, 80, 77); line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 20px; font-style: italic; font-weight: bold;\">100</span>句话 </span><span style=\"line-height: 18px; font-family: 微软雅黑,Microsoft YaHei; font-size: 16px; font-style: italic; font-weight: bold;\">屌丝画室：老师基本等于哑巴</span></p>', '2017-02-10 12:59:10');

